import{_ as i,c as a,a2 as p,o}from"./chunks/framework.BDo7hwTO.js";const _=JSON.parse('{"title":"2024-信息资源建设（国考卷）","description":"","frontmatter":{},"headers":[],"relativePath":"docs/信息资源建设/2024-10-信息资源建设国考.md","filePath":"docs/信息资源建设/2024-10-信息资源建设国考.md"}'),t={name:"docs/信息资源建设/2024-10-信息资源建设国考.md"};function e(r,l,s,d,n,c){return o(),a("div",null,l[0]||(l[0]=[p('<h1 id="_2024-信息资源建设-国考卷" tabindex="-1">2024-信息资源建设（国考卷） <a class="header-anchor" href="#_2024-信息资源建设-国考卷" aria-label="Permalink to &quot;2024-信息资源建设（国考卷）&quot;">​</a></h1><h2 id="选择题" tabindex="-1">选择题 <a class="header-anchor" href="#选择题" aria-label="Permalink to &quot;选择题&quot;">​</a></h2><ol><li>信息的本质特征?</li></ol><p>信息的本质特征是<code>传递性</code>。</p><ol start="2"><li>1YB = ?</li></ol><p>1YB = 1024ZB = 1024 * 1024 EB = 1024 * 1024 * 1024 PB</p><ol start="3"><li>国家信息化的核心任务是?</li></ol><p>国家信息化的核心任务是<code>信息资源的开发和利用</code>。</p><ol start="4"><li>属于第三产业的是</li></ol><p>第一产业是指人类可以直接从自然界中取得产品以满足自己最基本需要的产业部门。主要指农业，包括种植业、畜牧业、渔业、林业，狩猎业等。 第二产业是指人们利用自然资源经过加工取得产品以满足自己进一步需要的产业部门。主要指工业，包括制造业、采掘业、矿业、建筑业、以及煤气、电力、自来水等。 第三产业是指为人类生产、生活和社会发展提供服务以满足自己更多需要的产业部门。通常是指服务业，包括内外贸易业、金融保险业、房地产业以及运输业、邮电业、公用事业、科学研究、文化、娱乐、教育等部门。</p><ol start="5"><li>Autonomy 的知识管理产品是?</li></ol><p>Autonomy的知识管理产品是<code>Concept Agents</code>。</p><ol start="6"><li><p>最早、最简单的组织结构形式是？</p></li><li><p>中国强制性国家标准代号是?</p></li><li><p>信息资源分析包括?</p></li><li><p>采集政治、经济、军事等方面的方法是？</p></li><li><p>新课题研究最好的检索方法是？</p></li><li><p>常用于预测需求、生产和销售等短期经济问题的方法是?</p></li><li><p>项目进展计划最简单的一种进度计划表是？</p></li><li><p>信息系统所采取的安全策略是？</p></li><li><p>SET 协议密钥位数是?</p></li><li><p>教育网站域名后缀是?</p></li><li><p>计算机对信息进行保护的最可靠的方法是?</p></li><li><p>以因特网为主要传播载体的新媒体是？</p></li><li><p>通过文本分析，图像识别等方法来阻挡不适宜信息的过滤方式是？</p></li><li><p>属于实物型信息资源采集渠道的是？</p></li><li><p>信息资源利用的基本途径是？</p></li></ol><h2 id="名词解析" tabindex="-1">名词解析 <a class="header-anchor" href="#名词解析" aria-label="Permalink to &quot;名词解析&quot;">​</a></h2><ol start="21"><li>国家信息化</li></ol><p>解答: 在国家统一规划和组织下，在农业、工业、科学技术、国防以及社会生活各个方面应用现代信息技术，深入开发、广泛应用信息资源，加速国家实现现代化的进程。</p><ol start="22"><li><p>信息资源检索</p></li><li><p>特尔斐法</p></li><li><p>人力资源平衡法</p></li><li><p>软件过程能力成熟模型 CMM</p></li></ol><h2 id="判断改错题" tabindex="-1">判断改错题 <a class="header-anchor" href="#判断改错题" aria-label="Permalink to &quot;判断改错题&quot;">​</a></h2><ol start="26"><li><p>知识管理的核心技术是文本挖掘技术。</p></li><li><p>定题服务 SDI 分为标准 SDI 和用户委托 SDI。</p></li><li><p>风险分析</p></li><li><p>主动攻击</p></li><li><p>D 级又称为验证保护级</p></li></ol><h2 id="简答题" tabindex="-1">简答题 <a class="header-anchor" href="#简答题" aria-label="Permalink to &quot;简答题&quot;">​</a></h2><ol start="31"><li><p>简述知识管理的含义、目标以及核心。</p></li><li><p>简述信息分析法概念以及组成。</p></li><li><p>简述信息资源传递以及特征</p></li><li><p>简述项目管理概念以及内容</p></li><li><p>简述网络管理以及基本功能。</p></li></ol><h2 id="综合题" tabindex="-1">综合题 <a class="header-anchor" href="#综合题" aria-label="Permalink to &quot;综合题&quot;">​</a></h2><ol start="36"><li>求平均信息量</li></ol>',23)]))}const u=i(t,[["render",e]]);export{_ as __pageData,u as default};
